member.example:
LLLPG(lexer(inputSource(src), inputClass(LexerSource))) {
	static rule int ParseInt(string input) {
		var src = (LexerSource<UString>)input;
		@[ (d:='0'..'9' {$result = $result * 10 + (d - '0');})+ ];
	}
}

